// Global type definitions for path aliases

declare module '@/components/*' {
  const component: any;
  export default component;
}

declare module '@/stores/*' {
  const store: any;
  export default store;
}

declare module '@/hooks/*' {
  const hook: any;
  export default hook;
}

declare module '@/styles/*' {
  const styles: any;
  export default styles;
}

declare module '@/lib/*' {
  const lib: any;
  export default lib;
}
